script:
frappe.ui.form.on("Journal Entry Account", {
    custom_item: function(frm, cdt, cdn) {
        var row = locals[cdt][cdn];
        if (row.custom_item) {
            frappe.db.get_value("Item", row.custom_item, "default_journal_account", function(r) {
                if (r && r.default_journal_account) {
                    frappe.model.set_value(cdt, cdn, "account", r.default_journal_account);
                }
            });
        }
    }
});
